mapper.update:
update student
set IDENTIFY_CODE=#{identifyCode}
where stu_no=#{stuNo}

update professor
set IDENTIFY_CODE=#{identifyCode}
where prof_no=#{profNo}

update student
set IDENTITY_STATE = 'Y'
where stu_no=#{stuNo}

update professor
set IDENTITY_STATE = 'Y'
where prof_no=#{profNo}

mapper.select:
select count(*) from
employee e
professor p
student s
join official_info o on
e.emp_no= o.OFFICIAL_NO and emp_name=#{name}
p.prof_no= o.OFFICIAL_NO and prof_name=#{name}
s.stu_no= o.OFFICIAL_NO and name=#{name}
and o.ssn=#{ssn}

mapper.update:
   		update official_info
		set hp1 = #{hp1}, hp2 = #{hp2}, hp3 = #{hp3}, email1 =#{email1}, email2 =#{email2}, zipcode =#{zipcode}, 
		address=#{address}, addr_detail=#{addressDetail}
		where official_no=#{officialNo}
