mapper.select:
select *
from
(
select rownum as RNUM, A.*
from(

	select * from STUDENT_VIEW


	select * from STUDENT_VIEW
	where substr(stu_no,1,4) between #{startNo}
	and #{endNo}

		and stu_no=#{stuNo}


		and name like '%' || #{name} || '%'


		and faculty_no=#{facultyNo}


		and major=#{major}


		and state in (#{state1}

			,#{state2}


			,#{state3}


			,#{state4}


			,#{state5}


			,#{state6}

		)


order by stu_no
)A
)

where RNUM>#{firstRecordIndex}
  and RNUM<=#{firstRecordIndex }+ #{recordCountPerPage}

select.if:
select count(*) from STUDENT_VIEW
where substr(stu_no,1,4) between
#{startNo} and #{endNo}

	and stu_no=#{stuNo}


	and name like '%' || #{name} || '%'


	and faculty_no=#{facultyNo}


	and major=#{major}


	and state in (#{state1}

		,#{state2}


		,#{state3}


		,#{state4}


		,#{state5}


		,#{state6}

	)

mapper.update:
update student
set
state=#{state}
where stu_no=#{stuNo}

update student
set
name=#{name}, major=#{major}
where stu_no=#{stuNo}

update student
set major=#{major}

	, minor=#{minor}


	, minor=null

where stu_no=#{stuNo}

mapper.select:
select * from score_view where stu_no=#{stuNo} and total_grade is not null
order by open_date
and extract(year from open_date) || extract(month from open_date)=#{semester}
