mapper.select:
select *
from
(
select rownum as RNUM, A.*
from(

	select * from STUDENT_VIEW


	select * from STUDENT_VIEW
	where stu_no is not null

		and stu_no=#{stuNo}


		and name like '%' || #{name} || '%'


		and faculty_no=#{facultyNo}


		and major=#{major}


		and state in (#{state1}

			,#{state2}


			,#{state3}


			,#{state4}


			,#{state5}

		)


order by stu_no
)A
)

where RNUM>#{firstRecordIndex}
  and RNUM<=#{firstRecordIndex }+ #{recordCountPerPage}

select.if:
select count(*) from STUDENT_VIEW
where stu_no is not null

	and stu_no=#{stuNo}


	and name=#{name}


	and faculty_no=#{facultyNo}


	and major=#{major}


	and state in (#{state1}

		,#{state2}


		,#{state3}


		,#{state4}


		,#{state5}

	)

mapper.update:
update student
set state=#{state}
where stu_no=#{stuNo}
